import axios from 'axios';
import { createAsyncThunk } from '@reduxjs/toolkit';
import toast from 'react-hot-toast';

axios.defaults.baseURL = 'https://pokeapi.co/api/v2';

const getAllPokemons = createAsyncThunk('pokedex/getAllPokemons', async (_, thunkAPI) => {
  try {
    const { data } = await axios.get('/pokemon');
    toast.success('Pokemons!');

    const pokemonDetails = data.results.map(async (pokemon) => {
        const pokeDetail = await axios.get(`/pokemon/${pokemon.name}`)
        console.log('pokeDetail', pokeDetail);
        return pokeDetail.data
    })
    const pokemonDetails2 = []

    for (const pokemon of data.results) {
        const {data} = await axios.get(`/pokemon/${pokemon.name}`)
        pokemonDetails2.push(data)
    }

    console.log('pokemonDetails', pokemonDetails);
    console.log('pokemonDetails2', pokemonDetails2);
    return  pokemonDetails2
  } catch (error) {
    toast.error('Error');
    return thunkAPI.rejectWithValue();
  }
});

const pokedexOperations = {
    getAllPokemons
};

export default pokedexOperations;
