import styles from './Navbar.module.css';
import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  return (
    <header>
      <nav className={styles.nav}>
        <Link
          to="/"
          className={({ isActive }) =>
            isActive ? styles.activeNavLink : styles.navLink
          }
        >
          All Posts
        </Link>
        <Link
          to="/create"
          className={({ isActive }) =>
            isActive ? styles.activeNavLink : styles.navLink
          }
        >
          + Create New Post
        </Link>
      </nav>
    </header>
  );
};

export default Navbar;
