import axios from 'axios';
import { createAsyncThunk } from '@reduxjs/toolkit';
import toast from 'react-hot-toast';

axios.defaults.baseURL = 'https://pokeapi.co/api/v2';

const getAllPokemons = createAsyncThunk('pokedex/getAllPokemons', async (_, thunkAPI) => {
  try {
    const { data } = await axios.get('/pokemon');
    toast.success('Pokemons!');
    const pokemonDetails = data.results.map(async (pokemon) => {
        const pokeDetail =  await axios.get(`/${pokemon.name}`)
        console.log('pokeDetail', pokeDetail);
    })
    return  pokemonDetails
  } catch (error) {
    toast.error('Error');
    return thunkAPI.rejectWithValue();
  }
});

const pokedexOperations = {
    getAllPokemons
};

export default pokedexOperations;
